package com.javaex.ex04;

public class Ex05 {
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int sum =0;
		
		
		for( int i=1; i<=10; i++ ) {
			sum = sum+i;
			System.out.println(i+"까지 합은"+sum);
		
		}
		System.out.println("1부터 10까지 합은"+sum+"입니다.");
	}

}

